					'step 7'
					if(event.list.length){
						event.target=event.list.shift();
						event.target.chooseCard.apply(event.target,event._args).set(event._set);
					}
					else{
						for(var i=0;i<event.targets.length;i++){
							if(!event.result[i]){
								event.result[i]={};
							}
						}
						event.finish();
					}
